const StatCard = ({ title, value, subtitle, highlight }) => {
  return (
    <div className="stat-card">
      <p className="stat-title">{title}</p>
      <h2>{value}</h2>
      {subtitle && (
        <p className={highlight ? "highlight" : "muted"}>
          {subtitle}
        </p>
      )}
    </div>
  );
};

export default StatCard;
