import { useNavigate, useParams } from "react-router-dom";

export default function UserIndividualProfile() {
  const navigate = useNavigate();
  const { id } = useParams();

  // Temporary dummy data (replace with API later)
  const user = {
    id,
    name: "Alwin Sam",
    phone: "+91 7483746289",
    role: "Seller",
    status: "Unverified",
    email: "alwin.sam@gmail.com",
    avatar: "https://i.pravatar.cc/150?img=1",
    address: "Bangalore, Karnataka, India",
  };

  const handleApprove = () => {
    alert("User Approved");
    navigate("/admin/users");
  };

  const handleReject = () => {
    alert("User Rejected");
    navigate("/admin/users");
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* HEADER */}
      <div className="flex items-center gap-4 mb-8">
        <button
          onClick={() => navigate(-1)}
          className="text-sm text-blue-600 hover:underline"
        >
          ‚Üê Back
        </button>
        <h1 className="text-xl font-semibold text-blue-600">
          User Profile
        </h1>
      </div>

      {/* PROFILE CARD */}
      <div className="bg-white rounded-2xl shadow-md p-8">
        <div className="flex flex-col md:flex-row gap-8">
          {/* AVATAR */}
          <div className="flex-shrink-0">
            <img
              src={user.avatar}
              alt={user.name}
              className="w-32 h-32 rounded-full object-cover border"
            />
          </div>

          {/* DETAILS */}
          <div className="flex-1">
            <h2 className="text-2xl font-semibold">{user.name}</h2>
            <p className="text-gray-500 mt-1">{user.phone}</p>
            <p className="text-gray-500">{user.email}</p>

            <div className="flex gap-3 mt-4">
              <span className="px-3 py-1 rounded-md text-sm bg-blue-100 text-blue-600">
                {user.role}
              </span>
              <span
                className={`px-3 py-1 rounded-md text-sm ${
                  user.status === "Verified"
                    ? "bg-green-100 text-green-600"
                    : "bg-yellow-100 text-yellow-700"
                }`}
              >
                {user.status}
              </span>
            </div>

            <div className="mt-6">
              <h4 className="font-medium text-gray-700">Address</h4>
              <p className="text-gray-600 mt-1">{user.address}</p>
            </div>
          </div>
        </div>

        {/* ACTIONS */}
        <div className="flex justify-end gap-4 mt-10">
          <button
            onClick={handleReject}
            className="px-6 py-2 rounded-xl bg-red-100 text-red-600 font-medium hover:bg-red-200"
          >
            Reject
          </button>
          <button
            onClick={handleApprove}
            className="px-6 py-2 rounded-xl bg-green-600 text-white font-medium hover:bg-green-700"
          >
            Approve
          </button>
        </div>
      </div>
    </div>
  );
}
